import React from "react";
import "./styles.css";

import { useForm } from "react-hook-form";



const Form = () => {

    const [message, setMessage] = useState('');
    const { handleSubmit} = useForm();

    const onSubmit = (data, e) => {
        e.preventDefault();
        console.log(data);
        setMessage(`thank you ${data.name} for your message`);
        reset();
    }


    return (
        <>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input 
                type="text" 
                name="nome" 
                placeholder="Como gostaria de ser chamado?" 
                />
                    
                <input 
                type="text" 
                name="empresa" 
                placeholder="Em qual empresa vocÃª trabalha?" 
                />
                    
                <input 
                type="email" 
                name="email" 
                placeholder="Digite aqui o seu email" 
                />
                    
                <input 
                type="tel" 
                name="telefone" 
                placeholder="Qual o seu telefone?" 
                />
                    
                <input 
                type="button" 
                id="enviar_button" 
                value="ENVIAR" />
            </form>
            {message}
        </>
    )
}

export default Form
