import React, { useState } from "react";
import "./styles.css";



function Form() {

    const [nome, useNome] = useState("")

    const alterar = (event) => {
        setState(nome);
        alert();
    }


    return (
        <>
            <form>
                <input 
                type="text" 
                name="nome" 
                placeholder="Como gostaria de ser chamado?" 
                value={nome}
                />
                    
                <input 
                type="text" 
                name="empresa" 
                placeholder="Em qual empresa vocÃª trabalha?" 
                />
                    
                <input 
                type="email" 
                name="email" 
                placeholder="Digite aqui o seu email" 
                />
                    
                <input 
                type="tel" 
                name="telefone" 
                placeholder="Qual o seu telefone?" 
                />
                    
                <input 
                type="button" 
                id="enviar_button" 
                value="ENVIAR" 
                onClick={alterar}
                /> 
            </form>
        </>
    )
}

export default Form
