import React, {useState} from "react";
import "./styles.css";

// LINKS QUE DIRECIONAM PARA OUTRAS PÁGINAS DA SPA


// MOVE O USUÁRIO ATÉ A SECTION CLICADA NA NAVABAR
import { Link } from "react-scroll";

// IMAGENS
import logo from "../../Images/Header/logo-dynamox.png";

// REACT-ICONS
import { AiOutlineMenu } from "react-icons/ai";
import { AiFillCloseSquare } from "react-icons/ai";



function Header() {

    /* sidebar: valor | setSidebar: função que altera o estado | 3º: valor atual (false) */
    const [sidebar, setSidebar] = useState(false);

    /* showSidebar: função ser ativada no clique | setSidebar(!sidebar): vai alterar o valor atual de false para true ou vice-versa (false-true / true-false) */
    const showSidebar = () => setSidebar(!sidebar);

    return (
        <>
                        <div className="navbar">
                <div className="logo_and_hambmenu">
                    <div className="nav_logo_div">
                        <Link to="/">
                            <img src={logo} alt="logo" />
                        </Link>
                    </div>
                        <Link to="#" className="menu_symbol menu_symbol_1">
                            <span onClick={showSidebar}>
                                <AiOutlineMenu />
                            </span>
                        </Link>
                </div>


                <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
                        <div className="navbar-toggle">
                            <Link onClick={showSidebar} to="#" className="menu_symbol menu_symbol_2">
                                <AiFillCloseSquare />
                            </Link>
                        </div>
                        <div>
                            <Link to="/dynapredict">
                                DynaPredict
                            </Link>
                            <Link
                            activeClass="active"
                            to="home_section2"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                            >
                                Sensores
                            </Link>
                        <Link
                            activeClass="active"
                            to="home_section3"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                            >
                                Contato
                            </Link>
                            </div>
                </nav>
            </div>
        </>
    )
}

export default Header
