import React from "react";
import "./styles.css";

// LINKS QUE DIRECIONAM PARA OUTRAS PÁGINAS DA SPA
import { Link } from "react-router-dom";

// IMAGENS
import logo from "../../Images/Header/logo-dynamox.png";

// REACT-ICONS
import { AiOutlineMenu } from "react-icons/ai";
import { AiFillCloseSquare } from "react-icons/ai";



function Header() {

    /* sidebar: valor | setSidebar: função que altera o estado | 3º: valor atual (false) */
    const [sidebar, setSidebar] = useState(false);

    /* showSidebar: função ser ativada no clique | setSidebar(!sidebar): vai alterar o valor atual de false para true ou vice-versa (false-true / true-false) */
    const showSidebar = () => setSidebar(!sidebar);

    return (
        <>
            <div className="header">
                <div className="header_div_logo">
                    <Link to="/">
                        <img src={logo} />
                    </Link>
                </div>
            
                <nav>
                    <ul>

                    </ul>
                </nav>
            </div>
        </>
    )
}

export default Header
